var rules = {
  pattern: /[?&]keyword=([^&|]+)/,
  name: 'class.tit@tag.a.0@text',
  author: 'class.bookinfo@tag.a.0@text'
};
var opts = {
  nonfuzzy: true,
  less: false,
  charset: 'UTF-8'
};

function searchFilter(rules, opts, schList){
  //ä¾èµ–baseUrlã€resultå˜é‡ã€‚
  //return Array
  //schList List<Object>(result.toArray()): è¦è¿‡æ»¤çš„åˆ—è¡¨
  //pattern RegExp: è·å–URLå†…searchKeyçš„æ­£åˆ™è¡¨è¾¾å¼
  //name String|RegExp: ä¹¦åè§„åˆ™
  //author String|RegExp: ä½œè€…è§„åˆ™
  //charset String('UTF-8'): URLç¼–ç æ–¹å¼
  //nonfuzzy Boolean(true): åˆ é™¤æ¨¡ç³Šæœç´¢ç»“æœ
  //less Boolean(false): åªä¿ç•™ä¹¦åé•¿åº¦æœ€çŸ­çš„å›¾ä¹¦

  schList = schList === undefined ? result.toArray() : schList;
  opts.charset = opts.charset === undefined ? 'UTF-8' : opts.charset;
  opts.nonfuzzy = opts.nonfuzzy === undefined ? true : !!opts.nonfuzzy;
  opts.less = opts.less === undefined ? false : !!opts.less;

  function getStr(ele, index){
    var nrIsReg = (rules.name instanceof RegExp);
    var arIsReg = (rules.author instanceof RegExp); 
    if (!nrIsReg || !arIsReg) { java.setContent(ele); }
    return {
      name: nrIsReg ? (String(ele).match(rules.name)||['',''])[1] : String(java.getString(rules.name)),
     author: arIsReg ? (String(ele).match(rules.author)||['',''])[1] : String(java.getString(rules.author)),
      index: index
    };
  }

  try {
    var kw = (baseUrl.match(rules.pattern) || ['',''])[1];
    kw = Packages.java.net.URLDecoder.decode(kw, opts.charset);
    var books = schList.map((e,i) => getStr(e,i)).filter(v => !opts.nonfuzzy || v.name.includes(kw) || v.author.includes(kw));
    var list = [];
    var min = Math.min.apply(null, books.map(v => v.name.length));
    var length = Math.max(String(kw).length, min);
    books.forEach(v => {
      if (!opts.less || v.name.length === length){ list.push(schList[v.index]); }
    });
    return list;
  } catch(e) {
    return schList;
  }
}

searchFilter(rules, opts);
</js>

