ruleContent.content:
@js:

function getTranslation() {
	importClass(org.jsoup.Jsoup);
	let doc = Jsoup.parse(result).select('.forum-content p'),
	trans = (/trans='(\d+)/).test(result)?result.match(/trans='(\d+)/)[1]:'';

	//尝试获取翻译后的段落
	let TransParts = (function() {
		if(doc.toArray().length<=trans&&doc.toArray().length>1){
			let body = "plxf=getTranslation&plxa[]=" + trans;
			let host = "https://www.esjzone.cc";
			let url = (/esjzone\.cc/).test(baseUrl) ? baseUrl: (host + baseUrl);
			let options = {
				method: "post",
				body: body
			};
			url = url + "," + JSON.stringify(options);
			//url=url+'@'+body;
			let responseStr = String(java.ajax(url));
			if ((/JinJing/).test(responseStr)) {
				return responseStr.replace(/<.?JinJing>/g, '');
			}else return doc.html()
		}else return doc.html()
	})();

	let Html = TransParts;
	let hint = "无法显示内容，书籍可能下架或为其他站内容，请到原网页查看\n\n章节链接：\n" + baseUrl;
	let adstr = "(adsbygoogle = window.adsbygoogle || []).push({});";
	return (Html == null || Html == undefined || Html == '') ? hint: Html.replace(adstr, '');

}
result = (/\.google\.cn/).test(baseUrl) ? decodeURIComponent(baseUrl.replace(/^.+[&?]text=/, '')) : getTranslation();

ruleSearch.author:
tag.a.-1@href@js:
result=java.ajax('https:'+result);
var author=result.match(/作者是([^，]*)/);
var tag=result.match(/类型是([^，]*)/);
var cover=result.match(/src="([^"]*?jpg)/);
var intro=result.match(/<br>([^<]*)/);
java.put('tag',(tag) ? tag[1]:'');
java.put('cover',(cover) ? cover[1]:'');
java.put('intro',(intro) ? intro[1]:'');
(author) ? author[1]:'';

ruleSearch.bookList:
id.waterfall@.item
@js:
result.toArray().filter(div=>String(div.select('.chapter a').text()).trim()).slice(0, 5).map(o=> {
    var img = String(o.select('.pic img').attr('data-original'));
    return {
        name: o.select('.title a').text(),
        author: String(o.select('.nickname').text()).replace(/\s*\/.+/, '').trim(),
        cover: img && !img.includes('nocover') ? img: 'http://suo.im/6mlA9F',
        tags: String(o.select('.num a').toArray().map(o =>o.attr('title'))),
        latest: String(o.select('.chapter').text()).replace(/\s*\[(.+)\]/, '（$1）'),
        intro: String(o.select('.intro').text()).replace(/[\s\t]+/g, '').trim(),
        catalogUrl: o.select('.title a').attr('href')
    }
});

ruleToc.chapterList:
@js:
var J = org.jsoup.Jsoup.parse(result);
var ycnum = J.select('.ycnum').text();
var id = baseUrl.match(/(id=|\/)(\d+)/)[2];
var tocs;
if (ycnum) {
    ycnum = parseInt(String(ycnum)) + 30;
    tocs = org.jsoup.Jsoup.connect('https://www.xiashu.cc/api/ajax/zj?id=' + id + '&num=' + ycnum+'&order=asc').get().select('a[href]')
} else {
    tocs = J.select('#detaillist a[href]')
}
tocs.toArray().map(a =>({
    text: a.text(),
    href: a.attr('href')
})).filter(a =>a.href);

